function countFacebook(t,a){$.ajax({url:"https://graph.facebook.com/",dataType:"jsonp",data:{id:t}}).done(function(t){t.share&&t.share.share_count?$(a).text(t.share.share_count):$(a).text(0)}).fail(function(){$(a).text("0")})}function countHatebu(t,a){$.ajax({url:"https://api.b.st-hatena.com/entry.count?callback=?",dataType:"jsonp",data:{url:t}}).done(function(t){$(a).text(t||0)}).fail(function(){$(a).text("0")})}